// Update does not automatically update the record in case of default values. Use .Reload() to refresh the records.
func (o *Magazine) Update(exec boil.Executor, columns boil.Columns) error {
	var err error
	if err = o.doBeforeUpdateHooks(exec); err != nil {
		return err
	}
	key := makeCacheKey(columns, nil)
	magazineUpdateCacheMut.RLock()
	cache, cached := magazineUpdateCache[key]
	magazineUpdateCacheMut.RUnlock()

	if !cached {
		wl := columns.UpdateColumnSet(
			magazineAllColumns,
			magazinePrimaryKeyColumns,
		)

		if !columns.IsWhitelist() {
			wl = strmangle.SetComplement(wl, []string{"created_at"})
		}
		if len(wl) == 0 {
			return errors.New("models: unable to update magazine, could not build whitelist")
		}

		cache.query = fmt.Sprintf("UPDATE \"magazine\" SET %s WHERE %s",
			strmangle.SetParamNames("\"", "\"", 1, wl),
			strmangle.WhereClause("\"", "\"", len(wl)+1, magazinePrimaryKeyColumns),
		)
		cache.valueMapping, err = queries.BindMapping(magazineType, magazineMapping, append(wl, magazinePrimaryKeyColumns...))
		if err != nil {
			return err
		}
	}

	values := queries.ValuesFromMapping(reflect.Indirect(reflect.ValueOf(o)), cache.valueMapping)

	if boil.DebugMode {
		fmt.Fprintln(boil.DebugWriter, cache.query)
		fmt.Fprintln(boil.DebugWriter, values)
	}
	_, err = exec.Exec(cache.query, values...)
	if err != nil {
		return errors.Wrap(err, "models: unable to update magazine row")
	}

	if !cached {
		magazineUpdateCacheMut.Lock()
		magazineUpdateCache[key] = cache
		magazineUpdateCacheMut.Unlock()
	}

	return o.doAfterUpdateHooks(exec)
}

